import '../css/Post.css';
const Profile = () => {
	let userName = 'dapper developer';
	let userLocation = 'Kampala II Rd., Kampala';
	let userPhone = [ '0700-123-456', true, '0777-987-654', false ];
	let userDonations = [ `Kid's coloring books`, `Oranges (6)`, `Hugs`, `Blender` ];
	let userReqests = [ `Walk / Joging partner`, `laptop repair (Windows)` ];

	let userDonationsList = userDonations.map((donation) => (
		<li className="list-group-item no-border">
			<i className="bi bi-person-fill" />
			<span className="mx-2">{donation}</span>
			<i className="bi bi-person-fill" />
		</li>
	));
	let userRequestsList = userReqests.map((request) => (
		<li className="list-group-item no-border">
			<i className="bi bi-person-fill" />
			<span className="mx-2">{request}</span>
			<i className="bi bi-person-fill" />
		</li>
	));
	let userPhoneList = userPhone.map((phone) => (
		<li className="list-group-item no-border">
			<i className="bi bi-person-fill" />
			<span className="mx-2">{phone}</span>
			<i className="bi bi-person-fill" />
		</li>
	));

	return (
		<div className="card mt-5">
			<div>
				<ul className=" list-group list-group-flush">
					<li className="list-group-item no-border">
						<i className="bi bi-person-fill" />
						<span className="fst-italic mx-2">nickname:</span>
						<span className="btn btn-md">{userName}</span>
					</li>
					<li className="list-group-item no-border">
						<i className="bi bi-person-fill" />
						<span className="fst-italic mx-2">phone:</span>
						<span className="btn btn-md">{userPhone}</span>
						<span>
							<i className="bi bi-person-fill" />
							<span className="fst-italic mx-2">Whatsapp</span>
						</span>
					</li>
					<li className="list-group-item no-border">
						<i className="bi bi-person-fill" />
						<span className="fst-italic mx-2">location:</span>
						<span className="btn btn-md">{userLocation}</span>
					</li>
				</ul>
			</div>
			{/*we need a divider of some sort here*/}
			<div className="card-body">
				<ul className="list-group list-group-flush">{userDonationsList}</ul>
				{/* insert horizontal divider here*/}
				{/* list of needs dynamically*/}
				<ul className="list-group list-group-flush">{userRequestsList}</ul>
			</div>
		</div>
	);
};

export default Profile;
