import Navbar from './Navbar';
import Menu from './Menu';

const CreatePost = () => {
	let userName = 'SoAndSo';
	let userLocation = 'Rich people apartments, Muyenga';
	let isDonating = false;
	return (
		<div>
			<Navbar />
			<div className="container ">
				<div className="row-cols-1">
					<div className="m-auto col-12 col-sm-10 col-md-8 col-lg-6 col-xl-6 col-xxl-6">
						<div className="card mt-5">
							<div className="card-header bg-white d-grid">
								<span className="fst-italic mx-2">
									{userName} from {userLocation} is {isDonating ? 'donating' : 'requesting'}:
								</span>
							</div>
							<div className="card-body ">
								<div className="d-grid">
									<ul className="list-group list-group-flush">userDonationsList</ul>
									<button className="btn btn-outline-secondary fst-italic fs-5 mt-3 " type="button">
										have something? make a donation!
									</button>
								</div>
							</div>
							<div className="d-grid" />
						</div>
					</div>
				</div>
			</div>
			<Menu />{' '}
		</div>
	);
};

export default CreatePost;
