import '../css/Post.css';

const Post = () => {
	let imageUrl = 'https://picsum.photos/540/320?grayscale&random=8';
	let userLocation = 'Muyenga Tank Hill, Muyenga';
	let isItDonation = true;
	return (
		<div className="card mt-5">
			<div className="card-header">
				<i className="bi bi-arrow-up-right-circle" />
				{/* <i className="bi bi-arrow-down-left-circle" /> */}
				<span className="fst-italic mx-2">
					{isItDonation && 'Donation'} from {userLocation}.
				</span>
			</div>
			<img src={imageUrl} className="card-img-top" alt="S/O to Lorem Picsum" />
			<ul className="list-group list-group-flush">
				<li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Donation 1</span>
				</li>
				<li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Donation 2</span>
				</li>
				<li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Donation 3</span>
				</li>
				<li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Donation 4</span>
				</li>
			</ul>
			<div className="d-grid">
				<button className="btn btn-outline-secondary fst-italic fs-5" type="button">
					{isItDonation && 'I need this!'}
				</button>
			</div>
		</div>
	);
};

export default Post;
