import React, { useState } from 'react';
import '../css/Post.css';

const Post = () => {
	let imageUrl = 'https://picsum.photos/540/320?grayscale&random=' + Math.round(Math.random() * (19 - 10) + 1);
	let userLocation = 'Mutesa II Rd., Ntinda';
	let isItDonation = false;
	let userDonations = [
		[ `Kid's coloring books`, `Oranges (6)`, `Hugs`, `Blender` ],
		[ `Dancing shoes`, `Soap`, `old working Printer`, `5x6 duvet` ]
	];
	let userReqests = [
		[ `Walking partner`, `laptop repair (Windows)` ],
		[ `Homeschool teacher` ],
		[ `potted plants advice`, `teach me knitting`, `jogging partner` ]
	];
	const [ isOpened, setIsOpened ] = useState(false);
	function loadPostDetails() {
		setIsOpened((wasOpened) => !wasOpened);
		console.log('extra details loaded');
	}
	let userReqestsList = userReqests.forEach.map((request) => (
		<li className="list-group-item no-border">
			<i className="bi bi-arrow-right-square" />
			<span className="mx-2">{request}</span>
		</li>
	));
	return (
		<div className="card mt-5">
			<div className="card-header">
				{/* <i className="bi bi-arrow-up-right-circle" /> */}
				<i className="bi bi-arrow-down-left-circle" />
				<span className="fst-italic mx-2">
					{' '}
					{!isItDonation && 'Request'} from {userLocation}.
				</span>
			</div>
			<img src={imageUrl} className="card-img-top" alt="S/O to Lorem Picsum" />
			<ul className="list-group list-group-flush">
				{userReqestsList}
				{/* <li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Unmet need 2</span>
				</li>
				<li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Unmet need 3</span>
				</li>
				<li className="list-group-item  no-border">
					<i className="bi bi-arrow-right-square" />
					<span className="mx-2">Unmet need 4</span>
				</li> */}
				{isOpened && (
					<li className="list-group-item  no-border">
						<i className="bi bi-arrow-right-square" />
						<span className="mx-2">Extra details work!</span>
					</li>
				)}
			</ul>
			<div className="d-grid">
				<button className="btn btn-outline-secondary fst-italic fs-5" type="button" onClick={loadPostDetails}>
					{!isItDonation && 'I can help!'}
				</button>
			</div>
		</div>
	);
};

export default Post;
