import '../css/Post.css';

const Post = () => {
	let imageUrl = 'https://picsum.photos/540/320?grayscale&random=' + Math.round(Math.random() * (19 - 10) + 1);
	let userLocation = 'Mutesa II Rd., Ntinda';
	let isItDonation = false;
	let postDetails = false;
	let loadDetails = postDetails ? (
		<ul className="list-group list-group-flush">
			<li className="list-group-item no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 1</span>
			</li>
			<li className="list-group-item  no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 2</span>
			</li>
			<li className="list-group-item  no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 3</span>
			</li>
			<li className="list-group-item  no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 4</span>
			</li>
		</ul>
	) : (
		<ul className="list-group list-group-flush">
			<li className="list-group-item no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 1</span>
			</li>
			<li className="list-group-item  no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 2</span>
			</li>
			<li className="list-group-item  no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 3</span>
			</li>
			<li className="list-group-item  no-border">
				<i className="bi bi-arrow-right-square" />
				<span className="mx-2">Unmet need 4</span>
			</li>
		</ul>
	);

	return (
		<div className="card mt-5">
			<div className="card-header">
				{/* <i className="bi bi-arrow-up-right-circle" /> */}
				<i className="bi bi-arrow-down-left-circle" />
				<span className="fst-italic mx-2">
					{' '}
					{!isItDonation && 'Request'} from {userLocation}.
				</span>
			</div>
			<img src={imageUrl} className="card-img-top" alt="S/O to Lorem Picsum" />
			{loadDetails}

			<div className="d-grid">
				<button className="btn btn-outline-secondary fst-italic fs-5" type="button" onClick={loadDetails}>
					{!isItDonation && 'I can help!'}
				</button>
			</div>
		</div>
	);
};

export default Post;
